parameters.jsCode:
// Parse AI response
const aiResponse = $input.first().json.message.content;
const originalData = $('Webhook: Detect Task').first().json;

let parsed;
try {
  let jsonStr = aiResponse;
  if (aiResponse.includes('```json')) {
    jsonStr = aiResponse.split('```json')[1].split('```')[0].trim();
  } else if (aiResponse.includes('```')) {
    jsonStr = aiResponse.split('```')[1].split('```')[0].trim();
  }
  parsed = JSON.parse(jsonStr);
} catch (e) {
  parsed = {
    is_task: false,
    confidence: 0,
    reasoning: 'Failed to parse AI response: ' + e.message
  };
}

// All decisions delegated to bot (dedup, create, comment)
let action = 'ignored';
if (parsed.is_task && parsed.confidence >= 75) {
  action = 'auto_create';  // Bot handles dedup + create
} else if (parsed.is_task && parsed.confidence >= 50) {
  action = 'pending_confirmation';  // Bot shows buttons
} else {
  action = 'ignored';
}

return {
  json: {
    source: 'n8n_ai',
    event_type: 'task_detection_result',
    timestamp: new Date().toISOString(),
    detection: {
      is_task: parsed.is_task,
      confidence: parsed.confidence,
      task_data: parsed.task_data || null,
      reasoning: parsed.reasoning
    },
    original_message: {
      chat_id: originalData.chat.id,
      message_id: originalData.message.message_id,
      text: originalData.message.text,
      user_id: originalData.user.id,
      user_name: originalData.user.name
    },
    plane: originalData.plane,
    action_taken: action
  }
};
